import React, { useState, useEffect} from 'react'
import { Link, useNavigate } from 'react-router-dom'

export default function Login() {
  const [credentials, setcredentials] = useState({ email: "", password: "" })
  let navigate = useNavigate();
  const onChange = (event) => {
    setcredentials({ ...credentials, [event.target.name]: event.target.value })
  }

  function handleCallbackResponse(response) {
    console.log ("Encoded JWT ID token:" + response.credential) ;
    }

  useEffect (() => {
    /* global google */
    google.accounts.id.initialize({
    client_id: "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.apps.googleusercontent.com", 
    callback: handleCallbackResponse
    });
    google.accounts.id.renderButton (
    //  document.getElementById("gbtn"), { theme: "outline", size: "large"}
      );
      }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch("http://localhost:3000/api/loginuser/", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ email: credentials.email, password: credentials.password })
    });

    const json = await response.json();
    console.log(json);

    if (!json.success) {
      alert("Email or Password is incorrect");
    }
    else {
      localStorage.setItem("userEmail", credentials.email);
      localStorage.setItem("authToken", json.authToken);
      console.log(localStorage.getItem("authToken"));
      navigate("/");
    }
  }




  return (
    <div className='container'>
      <form onSubmit={handleSubmit}>

        <div className="mb-3">
          <label htmlFor="exampleInputEmail1" className="form-label">Email address</label>
          <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" name="email" value={credentials.email} onChange={onChange} />
          <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
        </div>

        <div className="mb-3">
          <label htmlFor="exampleInputPassword1" className="form-label">Password</label>
          <input type="password" className="form-control" id="exampleInputPassword1" name="password" value={credentials.password} onChange={onChange} />
        </div>
        <button type="submit" className="btn btn-primary">Submit</button>
        <div className="g-signin2 m-2" id ="gbtn" data-onsuccess="onSignIn">Sign-In With Google</div>
        <Link to="/createuser" className="m-2 btn btn-danger "> I'm a new User</Link>
      </form>
    </div>
  )
}
