import React, { useEffect, useState } from 'react';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';

export default function MyOrder() {
    const [orderData, setOrderData] = useState([]);

    const fetchMyOrder = async () => {
        const userEmail = localStorage.getItem('userEmail');
        if (userEmail) {
            try {
                const response = await fetch("http://localhost:3000/api/myOrderData", {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({
                        email: userEmail
                    })
                });

                const data = await response.json();
                setOrderData(data.orderData && data.orderData.order_data ? data.orderData.order_data : []);
            } catch (error) {
                console.error('Error fetching order data:', error);
            }
        }
    };

    useEffect(() => {
        fetchMyOrder();
    }, []);

    return (
        <div>
            <Navbar />
            <div className='container text-center'>
                <div className='row'>
                    {orderData.map((item, index) => (
                        <div key={index} className='col-12 col-md-6 col-lg-3'>
                            <div className="card mt-3 rounded" style={{ width: "16rem", maxHeight: "360px" }}>
                                <div className="card-body">
                                    <h5 className="card-title">{item.name}</h5>
                                    <div className='container w-100 p-0' style={{ height: "38px" }}>
                                        <span className='m-1'>{item.qty}</span>
                                        <span className='m-1'>{item.size}</span>
                                        <span className='m-1'>{item.Order_date}</span>
                                        <div className='d-inline ms-2 h-100 w-20 fs-5'>
                                            â‚¹{item.price}/-
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
            <Footer />
        </div>
    );
}
