import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import Badge from 'react-bootstrap/Badge';
import Modal from '../Modal';
import Cart from '../screens/Cart';
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import { useCart } from './ContextReducer'
export default function Navbar(props) {
  let data = useCart();
  const [cartView, setCartView] = useState(false);


  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("authToken");
    navigate("/login");
  }


  return (
    <div>
      <nav className="navbar navbar-expand-lg navbar-light bg-light fixed-top" >
        <div className="container-fluid">
          <Link className="navbar-brand fs-1 fst-bold" to="/">GoFood</Link>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav me-auto ">
              <li className="nav-item">
                <Link className="nav-link active fs-5" aria-current="page" to="/">Home</Link>
              </li>
              {(localStorage.getItem("authToken")) ?
                <li className="nav-item">
                  <Link className="nav-link active fs-5" aria-current="page" to="/myOrder">My Orders</Link>
                </li> : ""}



            </ul>

            {!(localStorage.getItem("authToken")) ?
              <div className='d-flex'>

                <Link className="btn bg-white text-success mx-1" to="/login">Login</Link>
                <Link className="btn bg-white text-success mx-1" to="/createuser">SignUp</Link>

              </div>
              :
              <div>
                <div className='btn bg-white text-success mx-2' onClick={() => { setCartView(true) }} >
                  <ShoppingCartIcon />{" "}
                  <Badge className='ms-2' pill bg='danger'> {data.length} </Badge>
                </div>
                {cartView ? <Modal onClose={() => setCartView(false)}>
                  <Cart></Cart>

                </Modal> : null}

                <div className='btn bg-info  mx-2' onClick={handleLogout} >
                <img className='rounded-circle' src="https://source.unsplash.com/random/1000x1000?boy" alt="" style={{ height: "25px", objectFit: "fill" }} /> Log Out
                </div>



              </div>
            }

          </div>
        </div>
      </nav>
    </div>
  )
}
