import React, { useEffect, useState } from 'react';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';

export default function MyOrder() {
    const [ordermyData, setOrdermyData] = useState({});

    const fetchMyOrder = async () => {
        try {
            const response = await fetch("http://localhost:3000/api/myOrderData", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    email: localStorage.getItem('userEmail')
                })
            });

            if (response.ok) {
                const data = await response.json();
                setOrdermyData(data);
            } else {
                console.error('Failed to fetch data');
            }
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    }

    useEffect(() => {
        fetchMyOrder();
    }, []);

    return (
        <div>
            <Navbar />
            <div className='container text-center'>
                <div className='row'>
                    {Object.keys(ordermyData).map((key) => {
                        const data = ordermyData[key];

                        if (data.orderData) {
                            return data.orderData.order_data
                                .slice(0)
                                .reverse()
                                .map((item, index) => {
                                    return item.map((arrayData, innerIndex) => {
                                        if (arrayData.Order_date) {
                                            return (
                                                <div className='m-auto mt-5' key={innerIndex}>
                                                    {arrayData.Order_date}
                                                    <hr />
                                                </div>
                                            );
                                        } else {
                                            return (
                                                <div className='col-12 col-md-6 col-lg-3' key={innerIndex}>
                                                    <div className="card mt-3 rounded " style={{ width: "16rem", maxHeight: "360px" }}>
                                                        <div className="card-body">
                                                            <h5 className="card-title">{arrayData.name}</h5>
                                                            <div className='container w-100 p-0' style={{ height: "38px" }}>
                                                                <span className='m-1'>{arrayData.qty}</span>
                                                                <span className='m-1'>{arrayData.size}</span>
                                                                <span className='m-1'>{data}</span>
                                                                <div className=' d-inline ms-2 h-100 w-20 fs-5' >
                                                                    ₹{arrayData.price}/-
                                                                </div>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            );
                                        }
                                    });
                                });
                        } else {
                            return null; // Handle the case where data.orderData is not available
                        }
                    })}
                </div>
            </div>
            <Footer />
        </div>
    )
}
