import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import Promise from 'bluebird';
export default function Signup() {
    const [credentials, setcredentials] = useState({ name: "", email: "", password: "", location: "" })
    const navigate = useNavigate();
    const onChange = (event) => {
        setcredentials({ ...credentials, [event.target.name]: event.target.value })
    }
    const [address, setAddress] = useState("");




    const handleClick = async (e) => {
        e.preventDefault();
        let navLocation = () => {
            return new Promise((res, rej) => {
                if (navigator && navigator.geolocation) {
                    navigator.geolocation.getCurrentPosition(
                        res,
                        (geolocationPositionError) => { 
                            rej(new Error(geolocationPositionError.message));
                        }
                    );
                } else {
                    rej(new Error('Geolocation is unsupported'));
                }
            });
        }
        try {
            let latlong = await navLocation();
            let latitude = latlong.coords.latitude;
            let longitude = latlong.coords.longitude;
            console.log(latitude, longitude);
            const response = await fetch("http://localhost:3000/api/getlocation", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ latlong: { latitude, longitude } })
            });
            const { location } = await response.json();
            console.log(location);
            setAddress(location);
            setcredentials({ ...credentials, location });
        } catch (error) {
            console.log(error);
            setAddress("");
        }
    }











    const handleSubmit = async (e) => {
        e.preventDefault();
        const response = await fetch("http://localhost:3000/api/createuser/", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ name: credentials.name, email: credentials.email, password: credentials.password, location: credentials.location })
        });

        const json = await response.json();
        console.log(json);
        if (json.success) {
            navigate("/login");
        }
        if (!json.success) {
            alert("Enter valid Credentials");
        }
    }


    return (

        <div className='container'>
            <form onSubmit={handleSubmit}>

                <div className="mb-3">
                    <label htmlFor="name" className="form-label">Name</label>
                    <input type="text" className="form-control" name="name" value={credentials.name} onChange={onChange} />

                </div>
                <div className="mb-3">
                    <label htmlFor="exampleInputEmail1" className="form-label">Email address</label>
                    <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" name="email" value={credentials.email} onChange={onChange} />
                    <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
                </div>

                <div className="mb-3">
                    <label htmlFor="exampleInputPassword1" className="form-label">Password</label>
                    <input type="password" className="form-control" id="exampleInputPassword1" name="password" value={credentials.password} onChange={onChange} />
                </div>
                <div className="mb-3">
                    <label htmlFor="exampleInputPassword1" className="form-label">Address</label>
                    <input type="text" className="form-control" id="exampleInputadress" name="address"  value={address} onChange={(e)=>setAddress(e.target.value)} />
                    <div className="m-3">
                        <button type="button" onClick={handleClick} name="location" className=" btn btn-success">Click for current Location </button>
                    </div>                
                    </div>
                <button type="submit" className="btn btn-primary">Submit</button>
                <Link to="/Login" className="m-3 btn btn-danger "> Already a user</Link>ß
            </form>
        </div>
    )
}
